var mongodb = require('mongodb');
var ObjectId = mongodb.ObjectId;
module.exports = function (condition) {
    var arr = Object.keys(condition);
    for (var i = 0; i < arr.length; i++) {
        if (arr[i] === '_id') {
            var key = arr[i];
            condition[key] = ObjectId(condition[key]);
            break;
        }
    }
    return condition;
};